pub fn dna_to_rna(dna: &str) -> String {
    dna.chars()
        .map(|x| match x {
            'T' => 'U',
            _ => x,
        })
        .collect()
}

#[cfg(test)]
mod tests {
    use super::dna_to_rna;

    #[test]
    fn returns_expected() {
        assert_eq!(dna_to_rna("TTTT"), "UUUU");
        assert_eq!(dna_to_rna("GCAT"), "GCAU");
        assert_eq!(dna_to_rna(""), "");
    }
}
